import React from "react";
import { Box, Typography, Grid, Paper } from "@mui/material";
import { Html, Css, Code, GitHub } from "@mui/icons-material"; // Import Material-UI icons

export const Experience = () => {
  return (
    <div>
<Box
        sx={{
          background: "linear-gradient(to bottom,#740f41, #4B0082, #000000)", // Gradient from pink to purple to black
          color: "#fff",
          p: 4,
          minHeight: "100vh",
        }}
      >
        <Typography
          variant="h4"
          component="h2"
          align="center"
          mb={4}
          style={{ fontWeight: "bold" }}
        >
          EXPERIENCE
        </Typography>
        <Grid container spacing={4} justifyContent="center" mb={6}>
          {/* Skills Section */}
          <Grid item xs={12} md={6}>
            <Typography variant="h5" align="center" mb={2}>
              Skills
            </Typography>
            <Grid container spacing={2} justifyContent="center">
              {[
                { name: "HTML", icon: <Html /> },
                { name: "CSS", icon: <Css /> },
                { name: "React", icon: <Code /> }, // Using Code icon for React
                { name: "GitHub", icon: <GitHub /> },
              ].map((skill, index) => (
                <Grid item key={index}>
                  <Box
                    display="flex"
                    flexDirection="column"
                    alignItems="center"
                    justifyContent="center"
                  >
                    <Box
                      bgcolor="#14213d"
                      borderRadius="50%"
                      width={80}
                      height={80}
                      display="flex"
                      alignItems="center"
                      justifyContent="center"
                      mb={1}
                    >
                      {skill.icon}
                    </Box>
                    <Typography variant="subtitle1" color="#fff">
                      {skill.name}
                    </Typography>
                  </Box>
                </Grid>
              ))}
            </Grid>
          </Grid>

          {/* Experience Section */}
          <Grid item xs={12} md={6}>
            <Typography variant="h5" align="center" mb={2}>
              Work Experience
            </Typography>
            <Grid container spacing={4} justifyContent="center">
              {[
                {
                  role: "Software Engineer",
                  company: "Google",
                  period: "Sept, 2022 - Present",
                  points: [
                    "Worked on Google Maps",
                    "Reduced load times by 50%",
                  ],
                  logo: "/icons/google.png",
                },
                {
                  role: "UI Designer",
                  company: "Microsoft",
                  period: "Aug, 2021 - Aug, 2022",
                  points: [
                    "Worked on Windows 11",
                    "Designed the control panel",
                  ],
                  logo: "/icons/microsoft.png",
                },
              ].map((job, index) => (
                <Grid item xs={12} key={index}>
                  <Paper
                    elevation={3}
                    style={{
                      padding: "16px",
                      backgroundColor: "#14213d",
                      borderRadius: "8px",
                    }}
                  >
                    <Box display="flex" alignItems="center" mb={2}>
                      <img
                        src={job.logo}
                        alt={`${job.company} logo`}
                        style={{ width: 40, height: 40, marginRight: "16px" }}
                      />
                      <Box>
                        <Typography
                          variant="h6"
                          style={{ fontWeight: "bold", color: "#fff" }}
                        >
                          {job.role}, {job.company}
                        </Typography>
                        <Typography variant="body2" color="#ccc">
                          {job.period}
                        </Typography>
                      </Box>
                    </Box>
                    <ul style={{ paddingLeft: "16px" }}>
                      {job.points.map((point, i) => (
                        <li key={i} style={{ color: "#fff" }}>
                          <Typography variant="body2">{point}</Typography>
                        </li>
                      ))}
                    </ul>
                  </Paper>
                </Grid>
              ))}
            </Grid>
          </Grid>
        </Grid>
      </Box>
    </div>
  );
};
